import React, { useState, useRef, useEffect } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { MessageCircle, X, Send, Bot, Sparkles } from 'lucide-react';
import GeminiService from '../services/GeminiService';

interface Message {
  id: string;
  text: string;
  isUser: boolean;
  timestamp: Date;
}

const AIAssistant: React.FC = () => {
  const { isDark } = useTheme();
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      text: "Hi! I'm your credit assistant! ðŸŽ‰ I can help you with credit questions, spending advice, and financial tips. What would you like to know?",
      isUser: false,
      timestamp: new Date()
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  const quickActions = [
    "How can I improve my credit score?",
    "What's my credit utilization?",
    "Should I pay off my credit card?",
    "What's the best card for groceries?"
  ];

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  const handleSendMessage = async () => {
    if (!inputText.trim()) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      isUser: true,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsTyping(true);

    try {
      // Get AI response using Gemini
      const response = await GeminiService.getGeneralCreditAdvice(inputText, []);

      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: response,
        isUser: false,
        timestamp: new Date()
      };

      setTimeout(() => {
        setMessages(prev => [...prev, aiMessage]);
        setIsTyping(false);
      }, 1000);
    } catch (error) {
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: "Sorry, I'm having trouble connecting right now. Please try again later! ðŸ˜…",
        isUser: false,
        timestamp: new Date()
      };

      setTimeout(() => {
        setMessages(prev => [...prev, errorMessage]);
        setIsTyping(false);
      }, 1000);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <>
      {/* Floating Assistant Button */}
      {!isOpen && (
        <div className="fixed bottom-6 right-6 z-50">
          <button
            onClick={() => setIsOpen(true)}
            className="glass rounded-full p-4 hover:scale-110 transition-all duration-300 group animate-float hover:animate-wiggle"
            aria-label="Open AI Assistant"
          >
            <div className="relative">
              {/* Cute Mascot SVG */}
              <svg
                width="32"
                height="32"
                viewBox="0 0 32 32"
                className="text-primary group-hover:scale-110 transition-transform animate-happy-bounce"
              >
                {/* Body - soft rounded shape */}
                <ellipse cx="16" cy="22" rx="9" ry="7" fill="currentColor" opacity="0.9" />

                {/* Head - larger and rounder */}
                <circle cx="16" cy="13" r="9" fill="currentColor" />

                {/* Blush cheeks */}
                <circle cx="11" cy="15" r="1.5" fill="#ff69b4" opacity="0.6" />
                <circle cx="21" cy="15" r="1.5" fill="#ff69b4" opacity="0.6" />

                {/* Eyes - bigger and friendlier */}
                <circle cx="13" cy="11" r="2" fill="white" />
                <circle cx="19" cy="11" r="2" fill="white" />
                <circle cx="13.5" cy="11.5" r="1" fill="#4a90e2" />
                <circle cx="19.5" cy="11.5" r="1" fill="#4a90e2" />
                <circle cx="14" cy="11.2" r="0.3" fill="white" />
                <circle cx="20" cy="11.2" r="0.3" fill="white" />

                {/* Gentle smile */}
                <path d="M12 15.5 Q16 16.5 20 15.5" stroke="white" strokeWidth="1.5" fill="none" strokeLinecap="round" />

                {/* Cute antenna with heart */}
                <line x1="16" y1="4" x2="16" y2="7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" />
                <path d="M14 2 Q16 0 18 2 Q16 4 14 2" fill="#ff69b4" />

                {/* Sparkles around */}
                <circle cx="6" cy="8" r="0.8" fill="#ffd700" opacity="0.8" className="animate-sparkle" style={{ animationDelay: '0s' }} />
                <circle cx="26" cy="10" r="0.6" fill="#ffd700" opacity="0.8" className="animate-sparkle" style={{ animationDelay: '0.5s' }} />
                <circle cx="8" cy="24" r="0.7" fill="#ffd700" opacity="0.8" className="animate-sparkle" style={{ animationDelay: '1s' }} />
                <circle cx="24" cy="26" r="0.5" fill="#ffd700" opacity="0.8" className="animate-sparkle" style={{ animationDelay: '1.5s' }} />
              </svg>

              {/* Notification dot */}
              <div className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
            </div>
          </button>
        </div>
      )}

      {/* Chat Window */}
      {isOpen && (
        <div className="fixed bottom-6 right-6 w-80 h-96 z-50 glass rounded-2xl shadow-2xl flex flex-col overflow-hidden">
          {/* Header */}
          <div className="flex items-center justify-between p-4 border-b border-white/10">
            <div className="flex items-center space-x-3">
              <div className="relative">
                <svg
                  width="24"
                  height="24"
                  viewBox="0 0 24 24"
                  className="text-primary"
                >
                  {/* Body */}
                  <ellipse cx="12" cy="16" rx="7" ry="5" fill="currentColor" opacity="0.9" />

                  {/* Head */}
                  <circle cx="12" cy="10" r="7" fill="currentColor" />

                  {/* Blush cheeks */}
                  <circle cx="8" cy="11" r="1" fill="#ff69b4" opacity="0.6" />
                  <circle cx="16" cy="11" r="1" fill="#ff69b4" opacity="0.6" />

                  {/* Eyes */}
                  <circle cx="9" cy="8" r="1.5" fill="white" />
                  <circle cx="15" cy="8" r="1.5" fill="white" />
                  <circle cx="9.5" cy="8.5" r="0.8" fill="#4a90e2" />
                  <circle cx="15.5" cy="8.5" r="0.8" fill="#4a90e2" />
                  <circle cx="9.8" cy="8.2" r="0.2" fill="white" />
                  <circle cx="15.8" cy="8.2" r="0.2" fill="white" />

                  {/* Gentle smile */}
                  <path d="M9 11.5 Q12 12.5 15 11.5" stroke="white" strokeWidth="1.2" fill="none" strokeLinecap="round" />

                  {/* Heart antenna */}
                  <line x1="12" y1="3" x2="12" y2="5" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" />
                  <path d="M10.5 1.5 Q12 0 13.5 1.5 Q12 3 10.5 1.5" fill="#ff69b4" />
                </svg>
                <div className="absolute -bottom-1 -right-1 w-2 h-2 bg-green-400 rounded-full"></div>
              </div>
              <div>
                <h3 className={`font-semibold ${isDark ? 'text-white' : 'text-gray-900'}`}>
                  Credit Assistant
                </h3>
                <p className={`text-xs ${isDark ? 'text-white/60' : 'text-gray-500'}`}>
                  Always here to help
                </p>
              </div>
            </div>
            <button
              onClick={() => setIsOpen(false)}
              className="p-1 hover:bg-white/10 rounded-lg transition-colors"
            >
              <X className="h-4 w-4 text-gray-400" />
            </button>
          </div>

          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {/* Quick Actions - only show if it's the first message */}
            {messages.length === 1 && (
              <div className="space-y-2">
                <p className={`text-xs font-medium ${isDark ? 'text-white/60' : 'text-gray-500'}`}>
                  Quick questions:
                </p>
                <div className="flex flex-wrap gap-2">
                  {quickActions.map((action, index) => (
                    <button
                      key={index}
                      onClick={() => setInputText(action)}
                      className={`px-2 py-1 text-xs rounded-lg transition-colors ${
                        isDark 
                          ? 'bg-white/10 text-white/80 hover:bg-white/20' 
                          : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                      }`}
                    >
                      {action}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {messages.map((message) => (
              <div
                key={message.id}
                className={`flex ${message.isUser ? 'justify-end' : 'justify-start'}`}
              >
                <div
                  className={`max-w-xs px-3 py-2 rounded-2xl ${
                    message.isUser
                      ? 'bg-primary text-white'
                      : isDark
                      ? 'bg-white/10 text-white'
                      : 'bg-gray-100 text-gray-900'
                  }`}
                >
                  <p className="text-sm">{message.text}</p>
                  <p className={`text-xs mt-1 ${
                    message.isUser ? 'text-white/70' : isDark ? 'text-white/50' : 'text-gray-500'
                  }`}>
                    {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </p>
                </div>
              </div>
            ))}

            {isTyping && (
              <div className="flex justify-start">
                <div className={`px-3 py-2 rounded-2xl ${
                  isDark ? 'bg-white/10' : 'bg-gray-100'
                }`}>
                  <div className="flex space-x-1">
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <div className="p-4 border-t border-white/10">
            <div className="flex space-x-2">
              <input
                ref={inputRef}
                type="text"
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask me anything about credit..."
                className={`flex-1 px-3 py-2 rounded-xl text-sm border-0 outline-none ${
                  isDark
                    ? 'bg-white/10 text-white placeholder-white/50'
                    : 'bg-gray-100 text-gray-900 placeholder-gray-500'
                }`}
              />
              <button
                onClick={handleSendMessage}
                disabled={!inputText.trim() || isTyping}
                className="btn-primary p-2 rounded-xl disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <Send className="h-4 w-4" />
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default AIAssistant;
